  //   },
  //   {
  //     "id": "2",
  //     "title": "at zurichberg",
  //     "description": "a beatifeaul place to chill",
  //     "latitude": 88.3434,
  //     "longtiude": 3.2323,
  //     "amountBenches": 4,
  //     "amountFirePlaces": 5,
  //     "amountTrashCans": 0,
  //     "distanceToNextShop": 150,
  //     "quietness": 2.0,
  //     "directions": null,
  //     "hasMeadow": true,
  //     "isLocationOnWater": true,
  //     "ratings":
