  //       ]
  //   },
  //   {
  //     "id": "3",
  //     "title": "at zurichberg",
  //     "description": "a beatifeaul place to chill",
  //     "latitude": 32.23232,
  //     "longtiude": 2.32434,
  //     "amountBenches": 10,
  //     "amountFirePlaces": 5,
  //     "amountTrashCans": 3,
  //     "distanceToNextShop": 10100,
  //     "quietness": 5.0,
  //     "directions": null,
  //     "hasMeadow": true,
  //     "isLocationOnWater": false,
  //     "ratings":
  //     [
  //       {
  //         "id": "7",
  //         "rating": 5

  //       },
  //       {
  //         "id": "8",
  //         "rating": 5

  //       },
  //       {
  //         "id": "9",
  //         "rating": 3

  //       }
  //     ]
  //   }
  // ];

  const [benches, setBenches] = useState([]);

  function getBenches() {
    axios.get('http://localhost:8080/benches')
      .then(res => {
        const data = res.data;
      //  console.log("res", data)
        setBenches(data);
      })
  }

  useEffect(() => {
    // Update the document title using the browser API
   getBenches();
   // eslint-disable-next-line
  }, []);

  function calRating(ratingArray) {
    var total = 0;
    var length = 0;
    //console.log("rating,", ratingArray)
    length = ratingArray.length;
    ratingArray.map(r => total += r.rating);

    let result = total/length;

    return Math.round(result*2)/2;   
  }

  function randomImg(width, height, key) {
    var source = 'https://source.unsplash.com/random/' + width + 'x' + height + '/?bench,park' + key;
    return source;
  }

  return (
    <Fragment>
      <Navbar />
      <Grid container spacing={3}>
        {benches.map((obj, i) => (
          <Grid item xs={4} key={i}>
            <PostCard
              //picture={getPicture()}
              id={obj.id}
              image={randomImg(500, 500, obj.id)}
              title={obj.title}
              description={obj.description}
              valueQuietness={obj.quiet}
              valueRating={calRating(obj.ratings)}
              amountBenches={obj.amountBenches}
              amountFirePlaces={obj.amountFirePlaces}
              amountTrashCans={obj.amountTrashCans}
              distanceToNextShop={obj.distanceToNextShop}
              directions={obj.directions}
              readOnly={true}
            />
          </Grid>
        ))}
      </Grid>
    </Fragment>
  );
};

export default HomePage;
