import React, { Fragment } from "react";
import PropTypes from "prop-types";
import * as Yup from "yup";
import { Grid } from "@material-ui/core";
import { getString } from "../../../config/language";
import NoaTextField from "../../atoms/noaTextField/NoaTextField";
import NoaTextButton from "../../atoms/noaButtons/noaTextButton/NoaTextButton";

const regexName = /^[a-zA-Z äöüéèàÜÖÄÉÈÀ,+-]+$/;

export const validationSchema = Yup.object().shape({
  title: Yup.string()
    .trim()
    .matches(regexName, "Please enter only names")
    .required("Title required"),
  description: Yup.string()
    .trim()
    .max(250, "Max. 250 characters")
});
