import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../../molecules/navbar/navbar";
import PostCard from "../../molecules/card/card";
import CardForm from "../../organisms/cardForm/cardForm";
import Grid from '@material-ui/core/Grid';
import Rating from "../../atoms/rating/rating";
import Typography from '@material-ui/core/Typography';

const CardPage = (props) => {

    const [editing, setEditing] = useState(false);
    const [postCardId, setPostCardId] = useState(props.match.params.id);



    function randomImg(width, height, key) {
        var source = 'https://source.unsplash.com/random/' + width + 'x' + height + '/?bench,park' + key;
        return source;
    }

    function calRating() {

        var total = 0;
        var length = 0;

        let array = benche.ratings;
        length = array.length;
        array.map(r => total += r.rating);

        let result = total / length;

        return Math.round(result * 2) / 2;

    }

    const [benche, setBenche] = useState([]);

    function getOneBench(id) {
        if (id !== 'new') {
            axios.get(`http://localhost:8080/benches/${id}`)
            .then(res => {
                const data = res.data;
                console.log("res one", data)
                return setBenche(data);
            })
        }

    }

    function deleteBench(id) {
        axios.delete(`http://localhost:8080/benches/${id}`)
            .then(res => {
                const data = res.data;
                console.log("res one", data)
                //  return setBenche(data);
                props.history.push(`/`);
            })
    }

    function editBench(id) {
        // axios.delete(`http://localhost:8080/benches/${id}`)
        //     .then(res => {
        //         const data = res.data;
        //         console.log("res one", data)
        //         //  return setBenche(data);
        //         props.history.push(`/`);
        //     })
        setEditing(true);
    }

    useEffect(() => {
        // Update the document title using the browser API
        getOneBench(postCardId);
    }, []);

    const [value, setValue] = useState();

    function rateBench(ratingDTO) {
        axios.post(`http://localhost:8080/ratings`, ratingDTO)
            .then(res => {
                const data = res.data;
                console.log("res one", data, ratingDTO)
                //setValue(value);
            })
    }

    const ratingDTO = {
        "rating": value,
        "bench": benche
    }

    return (
        <Fragment>
            <Navbar />
            <Grid container spacing={3} justify="center"
                alignItems="center">
                <Grid item xs={4}>
                    {editing || postCardId === 'new' ?
                    <CardForm bench={postCardId === 'new' ? {} : benche} setBench={postCardId === 'new' ? {} : setBenche} />
                    :
                    <PostCard
                        id={postCardId}
                        image={randomImg(500, 500, postCardId)}
                        deleteButton={true}
                        editButton={true}
                        title={benche.title}
                        valueQuietness={benche.quietness}
                        // valueRating={calRating(postCardId)}
                        amountBenches={benche.amountBenches}
                        amountFirePlaces={benche.amountFirePlaces}
                        amountTrashCans={benche.amountTrashCans}
                        distanceToNextShop={benche.distanceToNextShop}
                        directions={benche.directions}
                        readOnly={true}
                        deleteFunction={() => deleteBench(postCardId)}
                        editFunction={() => editBench(postCardId)}
                    />
                    }
                </Grid>

            </Grid>
        </Fragment>
    );
}

export default CardPage;
