import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../../molecules/navbar/navbar";
import PostCard from "../../molecules/card/card";
import CardForm from "../../organisms/cardForm/cardForm";
import Grid from '@material-ui/core/Grid';
import Rating from "../../atoms/rating/rating";
import Typography from '@material-ui/core/Typography';

const CardPage = (props) => {

    const [editing, setEditing] = useState(false);
    const [postCardId, setPostCardId] = useState(props.match.params.id);
    const [bench, setBench] = useState([]);

    function randomImg(width, height, key) {
        var source = 'https://source.unsplash.com/random/' + width + 'x' + height + '/?bench,park' + key;
        return source;
    }

    function calRating(ratingArray) {
        if (ratingArray === undefined) {
            return 0;
        } else {
            console.log(bench.ratings)
            var total = 0;
            var length = 0;
            console.log("rating,", ratingArray)
            length = ratingArray.length;
            ratingArray.map(r => total += r.rating);

            let result = total / length;

            return Math.round(result * 2) / 2;
        }

    }

    function calQuiet(ratingArray) {
        if (ratingArray === undefined) {
            return 0;
        } else {
            console.log(bench.ratings)
            var total = 0;
            var length = 0;
            console.log("rating,", ratingArray)
            length = ratingArray.length;
            ratingArray.map(r => total += r.rating);
            let result = total / length;

            return Math.round(result * 2) / 2;
        }
    }

    function getOneBench(id) {
        if (id !== undefined) {
            axios.get(`http://localhost:8080/benches/${id}`)
                .then((res) => {
                    setBench(res.data);
                })
        }
    }


    function deleteBench(id) {
        axios.delete(`http://localhost:8080/benches/${id}`)
            .then(() => {
                props.history.push(`/`);
            })
    }

    function editBench() {
        setEditing(true);
    }

    useEffect(() => {
        // Update the document title using the browser API
        getOneBench(postCardId);
    }, []);

    const [valueRating, setValueRating] = useState({});
    const [valueQuiet, setValueQuiet] = useState({});

    function postRating() {
        console.log('DA')
        // axios.post(`http://localhost:8080/ratings`, ratingDTO)
        //     .then(res => {
        //         setValueRating(res.data);
        //         putBenchByRating();
        //     })
    }

    function putBenchByRating() {
        axios.put(`http://localhost:8080/benches/${bench.id}/rating/${valueRating.id}`)
            .then(res => {
                setBench(res.data);
            })
    }

    function rateQuietnes(quietDTO) {
        axios.post(`http://localhost:8080/quiets`, quietDTO)
            .then(res => {
                const data = res.data;
                console.log("post quiet", data, quietDTO)
                putQuietnessRatingInBench(data);
            })
    }

    function putQuietnessRatingInBench(quietDTO) {
        var benchId = bench.id;
        console.log("benchId", benchId);
        var quietId = quietDTO.id;
        console.log("quietId", quietId);
        axios.put(`http://localhost:8080/benches/${benchId}/quiet/${quietId}`)
            .then(res => {
                const data = res.data;
                console.log("put quiet", data, quietDTO)
                //setValue(value);
            })
    }

    const ratingDTO = {
        "rating": null,
    }

    const quietDTO = {
        "quiet": null,
    }
    return (
        <Fragment>
            <Navbar />
            {editing || postCardId === 'new' ?
                <Grid container spacing={3} justify="center" alignItems="center">
                    <Grid item xs={4}>
                        <CardForm bench={postCardId === 'new' ? {...bench, id: 'new'} : bench} setBench={postCardId === 'new' ? () => { } : setBench} />
                    </Grid>
                </Grid>

                :
                <Grid container spacing={3} justify="center" alignItems="center">
                    <Grid item md={4}>
                        <PostCard
                            id={postCardId}
                            image={randomImg(500, 500, postCardId)}
                            deleteButton={true}
                            editButton={true}
                            title={bench.title}
                            valueQuietness={bench.quiet}
                            valueRating={calRating(bench.ratings)}
                            amountBenches={bench.amountBenches}
                            amountFirePlaces={bench.amountFirePlaces}
                            amountTrashCans={bench.amountTrashCans}
                            distanceToNextShop={bench.distanceToNextShop}
                            directions={bench.directions}
                            readOnly={true}
                            deleteFunction={() => deleteBench(postCardId)}
                            editFunction={() => editBench(postCardId)}

                        />
                    </Grid>
                    <Grid item md={4}>
                        <Typography variant="body2" color="textSecondary" component="h6">
                            Rate
                        </Typography>
                        <Rating name="rating-feedback"
                            precicion={0.5}
                            value={valueRating}
                            onChange={(event, newValue) => {
                                setValueRating(newValue);
                                console.log('even ', event.target.value)

                            }}
                        />

                        <Typography variant="body2" color="textSecondary" component="h6">
                            Quiet
                        </Typography>
                        <Rating name="quiet-feedback"
                            value={valueQuiet}
                            onChange={(event, newValue) => {
                                console.log("quiet value, ", newValue)

                                quietDTO.quiet = newValue;
                                console.log("quietDTO", quietDTO)
                                setValueQuiet(newValue)
                                rateQuietnes(quietDTO)
                            }}
                        />
                    </Grid>
                </Grid>
            }
        </Fragment>
    );
}

export default CardPage;
