import React, { Fragment, useState } from "react";
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import CommentField from "../../molecules/commentField/CommentField";
import { Grid } from "@material-ui/core";


const CommentModal = ({comments, props}) => {

    return (
        <Paper>
            <Grid
                container
                direction="column"
                justify="center"
                alignItems="flex-start"
            >
                <Grid>
                    <CommentField username={props.usernameCurrentUser} firstName={props.firstNameCurrentUser} lastName={props.lastNameCurrentUser} />
                </Grid>
                <Grid>

                    <Typography variant="h3" gutterBottom>Comments</Typography>
                </Grid>

                <List className={classes.root}>
                    {comments.map((obj, i) => (
                        <Grid item>
                            <ListItem alignItems="flex-start">
                                <ListItemAvatar>
                                    <Avatar className={classes.avatar}>
                                        {obj.user.firstName.substring(0, 1).toUpperCase() + obj.user.lastName.substring(0, 1).toUpperCase()}
                                    </Avatar>
                                </ListItemAvatar>
                                <ListItemText
                                    primary={obj.user.username}
                                    secondary={
                                        <React.Fragment>
                                            <Typography
                                                component="span"
                                                variant="body2"
                                                className={classes.inline}
                                                color="textPrimary"
                                            >
                                            </Typography>
                                            {obj.commentText}
                                        </React.Fragment>
                                    }
                                />
                            </ListItem>
                            <Divider variant="inset" component="li" />
                        </Grid>
                    ))}


                </List>
            </Grid>
        </Paper>
    )

}
export default CommentModal;
