import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../../molecules/navbar/navbar"
import PostCard from "../../molecules/card/card"
import Grid from '@material-ui/core/Grid';
import { Formik, yupToFormErrors } from "formik";
import * as Yup from "yup";
import { TextField } from "@material-ui/core";
import useStyle from "./BenchFormStyle";
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Button from '@material-ui/core/Button';

const BenchForm = ({ bench, setBench }) => {

    const [hasMeadow, setHasMeadow] = useState(false);
    const [isLocationOnWater, setIsLocationOnWater] = useState(false);
    const [submitting, setSubmitting] = useState(false);

    const handleChangeMeadow = () => {
        setHasMeadow(!hasMeadow);
    }

    const handleChangeLocationOnwater = () => {
        setIsLocationOnWater(!isLocationOnWater);
    }


    return (
        <Formik
            initialValues={{
                id: bench.id,
                title: bench.title,
                description: bench.description,
                latitude: bench.latitude,
                longitude: bench.longitude,
                amountBenches: bench.amountBenches,
                amountFirePlaces: bench.amountFirePlaces,
                amountTrashCans: bench.amountTrashCans,
                distanceToNextShop: bench.distanceToNextShop,
                quiet: bench.quiet,
                directions: bench.directions,
                hasMeadow: bench.hasMeadow,
                isLocationOnWater: bench.isLocationOnWater
            }}
            enableReinitialize={true}
            validationSchema={null}
            onSubmit={values => {
                setSubmitting(true);
                const dto = { ...bench, ...values };
                if (bench.id !== 'new') {
                    axios.put(`http://localhost:8080/benches/${bench.id}`, dto)
                        .then(response => {
                            setSubmitting(false);
                            setBench(response.data)
                        })
                        .catch(error => {
                            setSubmitting(false);
                            console.error('Error in Put', error);
                        });
                } else {
                    axios.post(`http://localhost:8080/benches`, dto)
                        .then(response => {
                            setSubmitting(false);
                            setBench(response.data);
                        })
                        .catch(error => {
                            setSubmitting(false);
                            console.error('Error in Post', error);
                        })
                }
            }}
        >
            {({ handleSubmit, errors, touched, handleChange }) => {
                return (
                    <Fragment>
                        <Navbar />
                        <div style={{ padding: 50 }}>
                            <form method="post" onSubmit={handleSubmit} onChange={handleChange}>
                                <Grid
                                    container
                                    direction="row"
                                    justify="center"
                                    alignItems="center"
                                    spacing={3}
                                >
                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-basic"
                                            name="title"
                                            label="Title"
                                            error={errors.title && touched.name}
                                            helperText={touched.title ? errors.title : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-basic"
                                            name="description"
                                            label="Description"
                                            multiline
                                            error={errors.description && touched.description}
                                            helperText={touched.description ? errors.description : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-basic"
                                            name="latitude"
                                            label="Latitude"
                                            error={errors.latitude && touched.latitude}
                                            helperText={touched.latitude ? errors.latitude : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-basic"
                                            name="longitude"
                                            label="Longitude"
                                            error={errors.longitude && touched.longitude}
                                            helperText={touched.longitude ? errors.longitude : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-number"
                                            name="amountBenches"
                                            label="Amount benches"
                                            type="number"
                                            error={errors.amountBenches && touched.amountBenches}
                                            helperText={touched.amountBenches ? errors.amountBenches : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-number"
                                            name="amountFirePlaces"
                                            label="Amount fire places"
                                            type="number"
                                            error={errors.amountFirePlaces && touched.amountFirePlaces}
                                            helperText={touched.amountFirePlaces ? errors.amountFirePlaces : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-number"
                                            name="amountTrashCans"
                                            label="Amount trash cans"
                                            type="number"
                                            error={errors.amountTrashCans && touched.amountTrashCans}
                                            helperText={touched.amountTrashCans ? errors.amountTrashCans : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-basic"
                                            name="distanceToNextShop"
                                            label="Distance to next shop"
                                            error={errors.distanceToNextShop && touched.distanceToNextShop}
                                            helperText={touched.distanceToNextShop ? errors.distanceToNextShop : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-number"
                                            name="quiet"
                                            label="Quiet"
                                            type="number"
                                            error={errors.quiet && touched.quiet}
                                            helperText={touched.quiet ? errors.quiet : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        <TextField
                                            id="standard-basic"
                                            name="directions"
                                            label="Directions"
                                            multiline
                                            error={errors.directions && touched.directions}
                                            helperText={touched.directions ? errors.directions : null}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        Are there any meadows?
                                            <Switch
                                            checked={hasMeadow}
                                            onChange={handleChangeMeadow}
                                            color="primary"
                                            name="hasMeadow"
                                            inputProps={{ 'aria-label': 'primary checkbox' }}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={4}>
                                        Is location on any water?
                                            <Switch
                                            checked={isLocationOnWater}
                                            onChange={handleChangeLocationOnwater}
                                            color="primary"
                                            name="isLocationOnWater"
                                            inputProps={{ 'aria-label': 'primary checkbox' }}
                                        />
                                    </Grid>
                                    <Grid item xs={4}/>

                                    <Grid item xs={4}/>
                                    <Grid item xs={2}>
                                       <Button variant="contained" disabled={submitting} color="primary" type="submit">
                                            Submit
                                       </Button>
                                    </Grid>
                                    <Grid item xs={2}>
                                       <Button variant="contained" color="secondary" type="reset">
                                            Reset
                                       </Button>
                                    </Grid>
                                    <Grid item xs={4}/>

                                </Grid>
                            </form>
                        </div>
                    </Fragment>
                )
            }}
        </Formik>
    )
}

export default BenchForm;
