import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../../molecules/navbar/navbar"
import PostCard from "../../molecules/card/card"
import Grid from '@material-ui/core/Grid';


const HomePage = () => {

  const benches = [
    {
      "id": "1",
      "title": "at zurichsee",
      "description": "a beatifeaul place to chill",
      "latitude": 47.431263,
      "longtiude": 8.740566,
      "amountBenches": 1,
      "amountFirePlaces": 1,
      "amountTrashCans": 1,
      "distanceToNextShop": 3700,
      "quietness": 4.0,
      "directions": "Bushaltestelle: Agasul; Auto: 15 Minuten von Effrektion",
      "hasMeadow": false,
      "isLocationOnWater": false,
      "ratings":
        [
          {
            "id": "1",
            "rating": 4

          },
          {
            "id": "2",
            "rating": 3

          },
          {
            "id": "3",
            "rating": 5

          }
        ]
    },
    {
      "id": "2",
      "title": "at zurichberg",
      "description": "a beatifeaul place to chill",
      "latitude": 88.3434,
      "longtiude": 3.2323,
      "amountBenches": 4,
      "amountFirePlaces": 5,
      "amountTrashCans": 0,
      "distanceToNextShop": 150,
      "quietness": 2.0,
      "directions": null,
      "hasMeadow": true,
      "isLocationOnWater": true,
      "ratings":
        [
          {
            "id": "4",
            "rating": 3

          },
          {
            "id": "5",
            "rating": 2

          },
          {
            "id": "6",
            "rating": 1

          }
        ]
    },
    {
      "id": "3",
      "title": "at zurichberg",
      "description": "a beatifeaul place to chill",
      "latitude": 32.23232,
      "longtiude": 2.32434,
      "amountBenches": 10,
      "amountFirePlaces": 5,
      "amountTrashCans": 3,
      "distanceToNextShop": 10100,
      "quietness": 5.0,
      "directions": null,
      "hasMeadow": true,
      "isLocationOnWater": false,
      "ratings":
      [
        {
          "id": "7",
          "rating": 5

        },
        {
          "id": "8",
          "rating": 5

        },
        {
          "id": "9",
          "rating": 3

        }
      ]
    }
  ];

  const [picture, setPictures] = useState();

  function getPicture() {
    axios.get('https://source.unsplash.com/random/?park,bench')
      .then(res => {
        const pic = res.data;
        setPictures(pic);
        console.log(typeof (picture));
        return picture;
      })
  }

  function calRating(i) {

    var total = 0;
    var length = 0;

    let array = benches[i].ratings;
    length = array.length;
    array.map(r => total += r.rating);

    let result = total/length;

    return Math.round(result*2)/2;

  }

  function randomImg(width, height, key) {
    var source = 'https://source.unsplash.com/random/' + width + 'x' + height + '/?bench,park' + key;
    return source;
  }

  return (
    <Fragment>
      <Navbar />
      <Grid container spacing={3}>
        {benches.map((obj, i) => (
          <Grid item xs={4} key={i}>
            <PostCard
              //picture={getPicture()}
              id={i}
              image={randomImg(500, 500, i)}
              title={obj.title}
              description={obj.description}
              valueQuietness={obj.quietness}
              valueRating={calRating(i)}
              amountBenches={obj.amountBenches}
              amountFirePlaces={obj.amountFirePlaces}
              amountTrashCans={obj.amountTrashCans}
              distanceToNextShop={obj.distanceToNextShop}
              directions={obj.directions}
              readOnly={true}
            />
          </Grid>
        ))}
      </Grid>
    </Fragment>
  );
};

export default HomePage;
